import React,{useState} from "react";
import Notes from "./Notes";

function CreateArea() {

    const [title_text,setTitle] = useState("");
    const [content,setContent] = useState("");

    function handleTitleChange(event){
        const newTitle = event.target.value;
        setTitle(newTitle)
    }

    function handleContentChange(event){
        const newContent = event.target.value;
        setContent(newContent);
    }

       function handleClick(event){
            event.preventDefault();
    }



  return (
    <div>
      <form>
        <input onChange = {handleTitleChange} name="title" placeholder="Title" value = {title_text}/>
        <textarea onChange = {handleContentChange} name="content" placeholder="Take a note..." rows="3"  value = {content}/>
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
