import React,{useState} from "react";
import Header from "./Header.js";
import Footer from "./Footer.js";
import Notes from "./Notes.js";
import CreateArea from "./CreateArea.js";


function App(){

    const [notes,setNotes] = useState([]);

    function addNote(newNote){
        setNotes(function (prevNotes){
            return [...prevNotes,newNote]
        })  
    }

    function deleteNote(id){
        setNotes(function(prevNotes){
            return prevNotes.filter(function (item,note_id){
                return note_id !== id
            });
        });

    }

    return (
        <div>
          <Header />
          <CreateArea function_addNote = {addNote}/>
        {notes.map((n,index) => {
           return <Notes key={index} id = {index} note_title = {n.title}  note_content = {n.content} onDelete = {deleteNote}/>
         })
         }
          <Footer />
        </div>
    );

}

export default App;
