    return (
        <div>
          <Header />
          <CreateArea function_addNote = {addNote}/>
        {notes.map((n,index) => {
           return <Notes key={index} id = {index} note_title = {n.title}  note_content = {n.content}/>
         })
         }
          <Footer />
        </div>
    );

}

